root.script:
var team_number = 5;
var team_tab_size = 100 / team_number;

var map_info = Game.GetMapInfo();
if (map_info.map_display_name == "imba_overthrow") {
	$( "#MultiteamScoreboard" ).style.width = "86%";
	$( "#MultiteamScoreboard" ).style.horizontalAlign = "right";
	$( "#_dynamic_team_2" ).style.width = team_tab_size + "%";
	$( "#_dynamic_team_3" ).style.width = team_tab_size + "%";
	$( "#_dynamic_team_3" ).style.horizontalAlign = "left";
	$( "#_dynamic_team_3" ).style.marginLeft = team_tab_size + "%";

	$( "#_dynamic_team_6" ).SetAttributeInt( "team_id", 6 );
	$( "#_dynamic_team_6" ).style.width = team_tab_size + "%";
	$( "#_dynamic_team_6" ).style.marginLeft = team_tab_size * 2 + "%";
	$( "#_dynamic_team_7" ).SetAttributeInt( "team_id", 7 );
	$( "#_dynamic_team_7" ).style.width = team_tab_size + "%";
	$( "#_dynamic_team_7" ).style.marginLeft = team_tab_size * 3 + "%";
	$( "#_dynamic_team_8" ).SetAttributeInt( "team_id", 8 );
	$( "#_dynamic_team_8" ).style.width = team_tab_size + "%";
	$( "#_dynamic_team_8" ).style.marginLeft = team_tab_size * 4 + "%";

	$( "#ConquestScore_Radiant" ).style.visibility = "visible";
	$( "#ConquestScore_Dire" ).style.visibility = "visible";
	$( "#ConquestScore_Custom1" ).style.visibility = "visible";
	$( "#ConquestScore_Custom2" ).style.visibility = "visible";
	$( "#ConquestScore_Custom3" ).style.visibility = "visible";

	$( "#LogoAndScore2" ).AddClass("OverthrowScore")
	$( "#LogoAndScore3" ).AddClass("OverthrowScore")
	$( "#LogoAndScore6" ).AddClass("OverthrowScore")
	$( "#LogoAndScore7" ).AddClass("OverthrowScore")
	$( "#LogoAndScore8" ).AddClass("OverthrowScore")
}

$( "#_dynamic_team_2" ).SetAttributeInt( "team_id", 2 );
$( "#_dynamic_team_3" ).SetAttributeInt( "team_id", 3 );
